def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
//Getting response from methods def ResponseMessage = context.response def parsedJson = new groovy.json.JsonSlurper().parseText(ResponseMessage)
assert parsedJson.Result == "Success" assert parsedJson.Display_items == 1 assert parsedJson.Matched_items == 1 assert parsedJson.Error_code == null assert parsedJson.Items != null assert parsedJson.Error_str == null assert parsedJson.Error_str_detail == null assert parsedJson.Items.name == context.expand('${SetUpProperties#newService}') assert parsedJson.Items.srvId == 1 assert parsedJson.Items.status == "inactive" assert parsedJson.Items.crmCode.'12345' == context.expand('${SetUpProperties#valueCrmCode}') assert parsedJson.Items.family == "gpon" assert parsedJson.Items.parameters.trafficProfile == "200 Megas" assert parsedJson.Items.parameters.clientClass == "publico" assert parsedJson.Items.parameters.bindLanPortsType == "dynamic" assert parsedJson.Items.parameters.bindLanPorts == 1 assert parsedJson.Items.parameters.bindLanPortsMaxAmount == 4 assert parsedJson.Items.parameters.bindLanPortsTotal == 1
//Firt Convert String MaxCpe Porperty to Integer def newMaxCpeInt = (context.expand('${SetUpProperties#newMaxCpe}')) assert parsedJson.Items.parameters.maxCpe == newMaxCpeInt.toInteger() assert parsedJson.Items.parameters.reservedIpList[0] == null assert parsedJson.Items.parameters.wifi.status == "disabled" assert parsedJson.Items.parameters.gd.status == "enabled" assert parsedJson.Items.parameters.gd.ipv4.nat == "enabled" assert parsedJson.Items.parameters.gd.ipv4.ipMode == "DHCP" assert parsedJson.Items.parameters.gd.ipv4.address == null assert parsedJson.Items.parameters.gd.ipv4.mask == null assert parsedJson.Items.parameters.gd.ipv4.gw == null //assert parsedJson.Items.parameters.gd.ipv4.dns[1].'0' == "8.8.8.8" assert parsedJson.Items.parameters.gd.ipv6.status == "disabled"
log.info("Validaciones OK")

