assert parsedJson.Items.srvId == 1 assert parsedJson.Items.status == "inactive" assert parsedJson.Items.name == "hsi" assert parsedJson.Items.crmCode != null assert parsedJson.Items.crmCode["99999"] == "idSuscripcion" assert parsedJson.Items.parameters != null assert parsedJson.Items.parameters["trafficProfile"] == "test" assert parsedJson.Items.parameters["clientClass"] == "residencial" assert parsedJson.Items.parameters["bindLanPorts"] == [1,2,3,4] assert parsedJson.Items.parameters["maxCpe"] == 90 assert parsedJson.Items.parameters["reservedIpList"] == [] assert parsedJson.Items.parameters["wifi"] != null assert parsedJson.Items.parameters["gd"] != null

